  ) as s3 inner join
 /*------------------------------------------------------------------------*/

(select idEgresado as idEgresadoo,nombres,apellidos,fechaNac,edad,dni, sexo, direccion,telefono1,telefono2,correo,anioIngreso,anioEgreso,nroHijos,estadoCivil,estado 
