from preguntaegresado,pregunta,encuesta
where   preguntaegresado.idPregunta=pregunta.idPregunta and encuesta.idEncuesta=pregunta.idEncuesta  ) as s0 inner join
/*-----------------------------------------------------------------------------------------------------------------------*/
(select idEgresado ,idEscuela as idescuel ,idDistritoNac as idDistritoNa,idDistritoRec as idDistritoRe  from egresado where  egresado.estado='A') as s1
inner join
/*-----------------------------------------------------------------------------------------------------------------------*/
