 /*------------------------------------------------------------------------*/
(select escuela.idEscuela , facultad.idFacultad, facultad.descripcion as descripcion1,
facultad.estado as estado0, escuela.descripcion,escuela.estado as estado00
 from escuela, facultad 

where  escuela.idFacultad=facultad.idFacultad
  ) as s4 inner join
 /*------------------------------------------------------------------------*/


/*-----------------------------------------------------------------------------------------------------------------------*/
(select idEgresado as idEgresadoo,nombres, apellidos,fechaNac,edad,dni, sexo, direccion,telefono1,telefono2,correo,anioIngreso,anioEgreso,nroHijos,estadoCivil,estado as estado10
from egresado where   egresado.estado='A'  ) as s5 inner join
/*-----------------------------------------------------------------------------------------------------------------------*/
(select idRespuesta,grupo, descripcion as descripcion11,estado as estado11 from respuesta where  estado='A') as s6 inner join (select idPreguntaEgresado as idPreguntaEgresado1,afirmacion from preguntaegresado  ) as s7 

